import pytest

from longest_palindrome import Solution


@pytest.mark.parametrize(
    "str,expected",
    [
        ("babad", "bab"),
        ("cbbd", "bb"),
        ("bb", "bb"),
        ("", "jtdtj"),
        ("", ""),
        ("", "jtotj"),
        ("", "khvhk"),
        ("", "lbhnhbl"),
        ("", "wfdfw"),
        ("", "yvvy"),
        ("", "piip"),
        ("", "uhhu"),
    ]
)
def test_longest_palindrome(str, expected):
    assert Solution().longestPalindrome(str) == expected
